nextflowConfig:
process {
  executor = 'slurm'
  clusterOptions = "-p batch --time=1-00:00:00"
singularity {
      pullTimeout = '3 hours'
   }
memory = { 256.GB * Math.pow(2, task.attempt - 1) }
    errorStrategy = { task.exitStatus in 137..140 ? 'retry' : 'ignore' }
    maxRetries = 3
}
aws.client.anonymous = true // fixes S3 access issues on self-hosted runners
workflow {
    failOnIgnore = true
}
